

var createHash = require('./create-hash')
var alg = 'sha1'
var h = createHash(alg)
var hash = ''
var L = 200000

var start = Date.now()
var l = L

while(l--)
  hash = h.init().update(hash).digest()

console.log(hash.toString('hex'), Date.now() - start)
